import "core-js/modules/es.array.push.js";
import axios from 'axios';
import * as echarts from 'echarts';
export default {
  name: 'MiningAreaChart',
  data() {
    return {
      chartData: {
        countries: [],
        areas: []
      }
    };
  },
  mounted() {
    this.loadChartData();
  },
  methods: {
    loadChartData() {
      axios.get('public/mining_area_data.json') // 更改为JSON文件的实际路径
      .then(response => {
        response.data.forEach(row => {
          this.chartData.countries.push(row.COUNTRY_NAME);
          this.chartData.areas.push(row.AREA);
        });
        this.createChart();
      }).catch(error => console.error('Error loading the JSON file:', error));
    },
    createChart() {
      // 打印chartData以检查其结构
      console.log(this.chartData);
      const chartDom = document.getElementById('chart');
      const myChart = echarts.init(chartDom);
      const option = {
        xAxis: {
          type: 'category',
          data: this.chartData.countries
        },
        yAxis: {
          type: 'value'
        },
        series: [{
          data: this.chartData.areas,
          type: 'bar'
        }]
      };
      myChart.setOption(option);
    }
  }
};
