export default {
  name: 'WorldMap',
  mounted() {
    // 请确保 makeMap 函数适用于您的应用
    this.loadECharts().then(() => {
      this.makeMap('瑞士', 'map');
    });
  },
  methods: {
    loadECharts() {
      return new Promise((resolve, reject) => {
        const script = document.createElement('script');
        script.src = 'https://echarts-maps.github.io/echarts-countries-js/echarts.min.js';
        script.onload = () => {
          const mapScript = document.createElement('script');
          mapScript.src = 'https://echarts-maps.github.io/echarts-countries-js/echarts-countries-js/Switzerland.js';
          mapScript.onload = resolve;
          mapScript.onerror = reject;
          document.head.appendChild(mapScript);
        };
        script.onerror = reject;
        document.head.appendChild(script);
      });
    },
    makeMap(country, elementId) {
      // 假设 ECharts 脚本已经加载
      var chart = echarts.init(document.getElementById(elementId));
      var option = {
        title: {
          text: country + ' Map',
          left: 'center'
        },
        tooltip: {
          trigger: 'item'
        },
        geo: {
          map: country,
          // 确保这个名称与导入的地图数据匹配
          roam: true,
          // 允许缩放和拖动
          label: {
            emphasis: {
              show: false
            }
          },
          itemStyle: {
            normal: {
              areaColor: '#f4f4f4',
              borderColor: '#111'
            },
            emphasis: {
              areaColor: '#b1d0ec'
            }
          }
        }
      };
      chart.setOption(option);
    }
  }
};
