import echarts from 'echarts';
export default {
  name: 'WorldMap',
  mounted() {
    // 创建ECharts图表并指定容器
    const myChart = echarts.init(document.getElementById('worldMap'));

    // 示例数据，每个国家一个系列
    const data = [{
      name: '中国',
      value: [116.46, 39.92]
    }, {
      name: '美国',
      value: [-97.52, 38.74]
    }, {
      name: '巴西',
      value: [-47.92, -15.78]
    }
    // 添加更多国家的数据
    ];

    // 配置项
    const option = {
      tooltip: {
        trigger: 'item',
        formatter: '{b}'
      },
      series: data.map(item => ({
        type: 'scatter',
        coordinateSystem: 'geo',
        data: [item],
        symbolSize: 10,
        // 散点大小
        label: {
          show: true,
          formatter: '{b}',
          position: 'right'
        },
        itemStyle: {
          color: 'blue' // 散点颜色
        }
      })),
      geo: {
        map: 'world',
        // 使用世界地图
        roam: true,
        // 可以拖动和缩放地图
        zoom: 1.2,
        // 初始缩放级别
        label: {
          emphasis: {
            show: false
          }
        },
        itemStyle: {
          areaColor: 'lightgray',
          // 地图区域颜色
          borderColor: 'black' // 地图区域边框颜色
        }
      }
    };

    // 使用配置项设置图表
    myChart.setOption(option);
  }
};
