// src/main.js
import Vue from 'vue';
import App from './App.vue';
import store from './store';
import router from './router'; // 确保已经创建了router
import ElementUI from 'element-ui';
import 'element-ui/lib/theme-chalk/index.css';

Vue.use(ElementUI);

Vue.config.productionTip = false;

new Vue({
  store,
  router, // 添加 router
  render: h => h(App)
}).$mount('#app');
