import axios from 'axios';
import * as echarts from 'echarts';
export default {
  name: 'MiningAreaChart',
  data() {
    return {
      chartData: {
        countries: [],
        areas: []
      }
    };
  },
  mounted() {
    this.loadChartData();
  },
  methods: {
    loadChartData() {
      axios.get('public/mining_area_data.json').then(response => {
        this.chartData.countries = response.data.map(row => row.COUNTRY_NAME);
        this.chartData.areas = response.data.map(row => row.AREA);
        this.createChart();
      }).catch(error => console.error('Error loading the JSON file:', error));
    },
    createChart() {
      const chart = echarts.init(document.getElementById('chart'));
      const option = {
        title: {
          text: '各国矿区面积'
        },
        tooltip: {},
        xAxis: {
          type: 'category',
          data: this.chartData.countries
        },
        yAxis: {
          type: 'value'
        },
        series: [{
          type: 'bar',
          data: this.chartData.areas
        }]
      };
      chart.setOption(option);
    }
  }
};
chart.setOption({
  title: {
    text: 'ECharts 入门示例'
  },
  tooltip: {},
  xAxis: {
    data: ['衬衫', '羊毛衫', '雪纺衫', '裤子', '高跟鞋', '袜子']
  },
