import * as echarts from 'echarts/core';
import { ScatterChart } from 'echarts/charts';
import { TitleComponent, TooltipComponent, GridComponent, GeoComponent,
// 添加地理坐标系组件
DatasetComponent, TransformComponent } from 'echarts/components';
import { CanvasRenderer } from 'echarts/renderers';
echarts.use([TitleComponent, TooltipComponent, GridComponent, DatasetComponent, TransformComponent, ScatterChart, CanvasRenderer, GeoComponent // 使用地理坐标系组件
]);
export default {
  name: 'WorldMap',
  mounted() {
    var chart = echarts.init(this.$refs.chart);

    // 示例数据，每个国家一个系列
    var data = [{
      name: "中国",
      value: [116.46, 39.92]
    }, {
      name: "美国",
      value: [-97.52, 38.74]
    }, {
      name: "巴西",
      value: [-47.92, -15.78]
    }
    // 添加更多国家的数据
    ];
    var option = {
      tooltip: {
        trigger: "item",
        formatter: "{b}"
      },
      series: data.map(function (item) {
        return {
          type: "scatter",
          coordinateSystem: "geo",
          // 使用地理坐标系
          data: [item],
          symbolSize: 10,
          // 散点大小
          label: {
            show: true,
            formatter: "{b}",
            position: "right"
          },
          itemStyle: {
            color: "blue" // 散点颜色
          }
        };
      }),
      geo: {
        map: "world",
        // 使用世界地图
        roam: true,
        // 可以拖动和缩放地图
        zoom: 1.2,
        // 初始缩放级别
        label: {
          emphasis: {
            show: false
          }
        },
        itemStyle: {
          areaColor: "lightgray",
          // 地图区域颜色
          borderColor: "black" // 地图区域边框颜色
        }
      }
    };
    chart.setOption(option);
  }
};
