// router/index.js 或类似文件
import Vue from 'vue';
import VueRouter from 'vue-router';
import Home from '../views/Home.vue';
import MiningProductionCharts from 'src/components/MiningAreaChart.vue';

Vue.use(VueRouter);

const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home
  },
  {
    path: '/another-page',
    name: 'AnotherPage',
    component: AnotherPage
  },
  // 其他路由...
];

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
});

export default router;
